import React, { Component } from 'react';

class Wizards extends Component {
    constructor() {
        super();
        this.state = {
          wizards: []
        };
      }

    componentDidMount() {
        fetch('/players')
          .then(res => res.json())
          .then(wizards => this.setState({wizards}, () => console.log("We've got the following wizards ", wizards)));
      }
    render () {
        return (
            <div>
                <h3>
                    {this.state.wizards.map((wizard) =>  <li> {wizard.character} has {wizard.health} health, {wizard.gold} gold, {wizard.lightning} lightning 
                    and has the following cards <ul> {wizard.hand.map((card) => <li>{card.name} </li>)}
                    </ul>
                    </li>)}

                    </h3> 
                </div>
        );
    }
}

export default Wizards;
