import React, { Component } from 'react';

class HogwartsCards extends Component {
    constructor() {
        super();
        this.state = {
            hogwartscards: []

        }
    }
    componentDidMount() {
        fetch('/hogwartsCards')
            .then(res => res.json())
            .then(hogwartsCards => this.setState({ hogwartsCards }, () => console.log('Received the following hogwarts cards ', hogwartsCards)));
    }
    render() {
        if(hogwartsCards.length >= 1){
        let deck = this.state.HogwartsCards;
        let liveCards = []
        for (let i = 0; i < 5; i++) {
            liveCards.push(this.state.hogwartsCards[0])
            this.state.hogwartsCards.splice(0, 1)
        }
        console.log(deck);
        console.log(liveCards);
    }
        return (
            <div>
                {liveCards.map((card) => <li>{card.name}</li>)}
            </div>
        )
    };
}

export default HogwartsCards;
