import React, { Component } from 'react';
import Hand from './Hand'

class Wizards extends Component {
    constructor() {
        super();
        this.state = {
            wizards: [],
            currentSelection: 0
        };
    }
    componentDidMount(){
    fetch('/hogwartsCards')
    .then(res => res.json())
    .then(hogwartsCards => this.setState({ hogwartsCards }, () => console.log("hogwartsCards are ", hogwartsCards)));
}
    render() {
        let handObj = ""
        if(this.props.wizards){
        handObj = this.props.wizards.map((wizard, i) => <div key={i} > {wizard.number}{wizard.character} has {wizard.health} health, {wizard.gold} gold, {wizard.lightning} lightning, and has {wizard}
        <Hand playing={this.props.playing} hand={wizard.hand} />
        </div> )
        }
        return (
            <div className="Wizard">
                <div>
                    {handObj}
                </div>
            </div>
        );
    }
}
export default Wizards;
