import React, { Component } from 'react';

class HogwartsCards extends Component {
    constructor() {
        super();
        this.state = {
            hogwartsCards: []

        }
    }
    componentDidMount() {
        fetch('/hogwartsCards')
            .then(res => res.json())
            .then(hogwartsCards => this.setState({ hogwartsCards }, () => console.log('data is', hogwartsCards)));
    }
    render() {
        let theObject = []
        console.log('hoggy length is ', this.state.hogwartsCards)
        if (this.state.hogwartsCards.length > 1) {
            console.log('here');
            let liveCards = []
            for (let i = 0; i < 5; i++) {
                if (this.state.hogwartsCards[0]) {
                    liveCards.push(this.state.hogwartsCards[0])
                    this.state.hogwartsCards.splice(0, 1)
                }
            }
            console.log('here is the hoggies', liveCards)
            theObject = liveCards.map((card) => <li key={card}> {card.name}</li>)
        }

        return (
            <div>
                <div>
                    The hogwarts cards are
                {theObject}
                </div>
            </div>
        )
    };
}

export default HogwartsCards;
