import React, { Component } from 'react';

class Wizards extends Component {
    constructor() {
        super();
        this.state = {
            wizards: [],
            currentSelection: 0
        };
    }
    grabdetails() {
        fetch('/players')
            .then(res => res.json())
            .then(wizards => this.setState({ wizards }, () => console.log("We've got the following wizards ", wizards)));
    }

    componentDidMount() {
        this.grabdetails()
    }
    playing() {
        fetch('playcard', {
            headers: {
                'content-type': 'application/json'
            },
            method: 'POST',
            body: JSON.stringify({
                bontent: 'exists'
            })
        })
    }
    selectCard(uid) {
        this.setState({currentSelection: uid})
        console.log('well this happened')
    }
    cardClass(uid){
        let results = "card"
        if(uid === this.state.currentSelection){
            results = "activeCard"
        }
        return results
    }

    render() {

        console.log('the wizards are now ', this.state.wizards)
        return (
            <div className="Wizard">
                <div>
                    {this.state.wizards.map((wizard) => <div key={wizard.number}> {wizard.character} has {wizard.health} health, {wizard.gold} gold, {wizard.lightning} lightning
                    and has the following cards <div className="wizardHand"> {wizard.hand.map((card) => <div className={this.cardClass(card.uid)} onClick={() => this.playing(card.uid)}key={card.uid}> {card.name} </div>)}
                        </div>
                    </div>)}

                </div>
            </div>
        );
    }
}

export default Wizards;
