import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import Board from './components/board.js';
import Wizards from './components/wizards.js';
import HogwartsCards from './components/hogwartsCards.js';
import ChoiceModal from './components/choiceModal.js';
// import Logo from './components/logo';

class App extends Component {
  constructor() {
    super();
    this.state = {
      wizards: [],
      modalActive: false,
      cardChoiceCard: {},
      board: {
        villians: [],
        eventDeck: [],
        location: {
          name: "",
          villianCapacity: 0,
          villianCurrent: 0,
          locationNumber: 0,
          family: ""
        },
        locationCount: 1,
        locationLimit: 3
      },
      activePlayer: 0,
      alert: {
        show: false,
        text: ""
      }
    }
    this.choiceHandler = this.choiceHandler.bind(this);
  };
  componentDidMount() {
    fetch('/board')
      .then(res => res.json())
      .then(board => this.setState({ board }, () => console.log('The board state is ', board)));
  }
  playCard(card, actions) {
    fetch('playcard', {
      headers: {
        'content-type': 'application/json'
      },
      method: 'POST',
      body: JSON.stringify({
        cardUID: card.UID,
        actions: actions
      })
    })
    this.grabdetails()
  }
  grabdetails() {
    fetch('/players')
      .then(res => res.json())
      .then(wizards => this.setState({ wizards }, () => console.log("We've got the following wizards ", wizards)));
  }
  makeChoice(card) {
    this.setModal(true)
    this.setState({ cardChoiceCard: card })
  }
  setModal(state) {
    this.setState({ modalActive: state })
  }
  choiceHandler(card) {
    if (typeof variable !== 'undefined') {
      console.log(card);
      var action = 0
      if (card.decisions.exists) {
        this.makeChoice(card)
      } else { this.playCard(card, action) }
    }
  }
  componentDidMount() {
    this.grabdetails()
  }
  sendCardPurchase(card) {
    fetch('buycard', {
      headers: {
        'content-type': 'application/json'
      },
      method: 'POST',
      body: JSON.stringify({
        cardUID: card.UID
      })
    })
    this.grabdetails()
  }

  buyCard(card) {
    if (this.state.wizards[this.state.activePlayer].gold >= card.value) {
      this.sendCardPurchase(card);
    } else {
      let newText = "you can't buy " + card.name + " because you only have " + this.state.wizards[this.state.activePlayer].gold
      this.setState({
        alert: {
          show: true,
          text: newText
        }
      })
    }
  }

    render() {
      if (this.state.alert.show) {
        let alertObj = <div className='delete-button' onClick={() => { if (window.confirm('Yo, dis too many dolla')) this.onCancel(item) }} />
      } else { alertObj = "" }
      return (
        <div className="App">
          <header className="App-header">
            {/* <img src={logo} className="App-logo" alt="logo" /> */}
            <h1 className="App-title"> HPHB</h1>
            <div className="game">
              <Board borad={this.state.board} />
              <Wizards playing={this.choiceHandler} />
              <HogwartsCards />
              <ChoiceModal card={this.state.cardChoiceCard} unModal={this.setModal} play={this.choiceHandler.bind(this)} />
              {alertObj}
            </div>
          </header>

        </div>
      );
    }
  }

  export default App;
