import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import Board from './components/board.js';
import Wizards from './components/wizards.js';
import HogwartsCards from './components/hogwartsCards.js';
import choiceModal from './components/choiceModal.js';
// import Logo from './components/logo';

class App extends Component {
  constructor() {
    super();
    this.state = {
      wizards: [],
      modalActive: false,
      cardChoiceCard: {}
    };
  }
  grabdetails() {
    fetch('/players')
      .then(res => res.json())
      .then(wizards => this.setState({ wizards }, () => console.log("We've got the following wizards ", wizards)));
  }
  makeChoice(card) {
    this.setModal(true)
    this.setState({cardChoiceCard: card})
  }
  setModal(state) {
    this.setState({ modalActive: state })
  }
  choiceHandler(card) {
    var action = 0
    if (card.decisions.exists) {
      this.makeChoice(card)
    } else (this.playCard(card, action))
  }
  componentDidMount() {
    this.grabdetails()
  }
  playCard(card, actions) {
    fetch('playcard', {
      headers: {
        'content-type': 'application/json'
      },
      method: 'POST',
      body: JSON.stringify({
        cardUID: card.UID,
        actions: actions
      })
    })
    this.grabdetails()
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          {/* <img src={logo} className="App-logo" alt="logo" /> */}
          <h1 className="App-title"> HPHB</h1>
          <div className="game">
            <Board />
            <Wizards playing={this.choiceHandler} />
            <HogwartsCards />
            <choiceModal card={this.state.cardChoiceCard} unModal={this.setModal} />
          </div>
        </header>

      </div>
    );
  }
}

export default App;
