import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import Board from './components/board.js';
import Wizards from './components/wizards.js';
import HogwartsCards from './components/hogwartsCards.js';
// import Logo from './components/logo';

class App extends Component {
  constructor() {
    super();
    this.state = {
        wizards: [],
    };
}
grabdetails() {
    fetch('/players')
        .then(res => res.json())
        .then(wizards => this.setState({ wizards }, () => console.log("We've got the following wizards ", wizards)));
}
choiceSelector(actions){
  return actions
}
componentDidMount() {
    this.grabdetails()
}
  playing(card) {
    let actions = this.choiceSelector(this.state.wizards[this.activeWizard].hand)
    fetch('playcard', {
        headers: {
            'content-type': 'application/json'
        },
        method: 'POST',
        body: JSON.stringify({
            cardUID: UID,
            actionObject: actions
        })
    })
    this.grabdetails()
}
    render() {
      return (
        <div className="App">
          <header className="App-header">
            {/* <img src={logo} className="App-logo" alt="logo" /> */}
            <h1 className="App-title"> HPHB</h1>
            <div className="game">
          <Board/>
          <Wizards playing={this.playing}/>
          <HogwartsCards/>
          </div>
          </header>

        </div>
      );
    }
  }

  export default App;
