import React, { Component } from 'react';

class HogwartsCards extends Component {
    constructor() {
        super();
        this.state = {
            hogwartsCards: {
                faceUpCards:[],
                faceDownCount: 0
            },
            selectedCard: "",

        }
    }  
    selectCard(key) {
        this.setState = {selectedCard: key}
    }
    componentDidMount() {
        fetch('/hogwartsCards')
            .then(res => res.json())
            .then(hogwartsCards => this.setState({ hogwartsCards }, () => console.log('hogwartsCards are', hogwartsCards)));
    }
    render() {
        let theObject = ""
        if(this.props.hogwartsCards){
        theObject = this.props.hogwartsCards.faceUpCards.map((card) => <div className="card" onClick={() => this.props.buyCard(card)} key={card.name}> {card.name}</div>)
        }return (
            <div>
                <div>
                    The hogwarts cards are
                {theObject}
                </div>
            </div>
        )
    };
}

export default HogwartsCards;
