import React, { Component } from 'react';

class ChoiceModal extends Component {
    constructor() {
        super();
        this.state = {
        };
    }
    render() {
        return (
            <div className="">
                and has the following cards
                <div className="wizardHand"> {this.props.hand.map((card) =>
                    <div className={this.cardClass(card.cardType)}
                        onClick={() => this.props.playing(card)} key={card.UID}> {card.name}
                    </div>)}
                </div>
            </div>
        );
    }
}

export default ChoiceModal;
