import React, { Component } from 'react';

class HogwartsCards extends Component {
    constructor() {
        super();
        this.state = {}
    }
    componentDidMount() {
        fetch('/hogwartsCards')
          .then(res => res.json())
          .then(hogwartsCards => this.setState({hogwartsCards}, () => console.log('The board state is ', hogwartsCards)));
      }
    render() {
        let deck = this.state.HogwartsCards;
        let liveCards = []
        for(let i = 0; i < 5; i++){
            liveCards.push(this.state.hogwartsCards[0])
            this.state.hogwartsCards.splice(0,1)
        }
        console.log(deck);
        console.log(liveCards);
        return (
            <div>
                {liveCards.map((card) => <li>{card.name}</li> )}
            </div>
        )
    };
}

export default HogwartsCards;
