import React, { Component } from 'react';
import Hand from './Hand'

class Wizards extends Component {
    constructor() {
        super();
        this.state = {
            currentSelection: 0
        };
    }
    render() {
        let handObj = ""
        if(this.props.wizards[0]){
        handObj = this.props.wizards.map((wizard, i) => <div key={i}> {wizard.number}{wizard.character} has {wizard.health} health, {wizard.gold} gold, {wizard.lightning} lightning
        <Hand playing={this.props.playing} hand={wizard.hand} />
        </div> )
        }
        return (
            <div className="Wizard">
                <div>
                    {handObj}
                </div>
            </div>
        );
    }
}

export default Wizards;
