import React, { Component } from 'react';

class Board extends Component {
    constructor() {
        super();
        this.state = {
            board : {}
        };
      }

    componentDidMount() {
        fetch('/board')
          .then(res => res.json())
          .then(board => this.setState({board}, () => console.log('Customers fetched...', board)));
      }
    render () {
        return (
            <div>
<h3>
    The Current Location is {this.state.board.location.name}
    </h3>
                </div>
        );
    }
}

export default Board;
