import React, { Component } from 'react';

class Hand extends Component {
    constructor() {
        super();
        this.state = {
            Hand: [],
            currentSelection: 0,
            Wizard
        };
    }
    selectCard(UID) {
        this.setState({ currentSelection: UID })
        console.log('well this happened')
    }
    cardClass(UID) {
        let results = "card"
        if (UID === this.state.currentSelection) {
            results = "activeCard"
        }
        return results
    }

    render() {
        return (
            <div className="Hand">
                <div>
                    and has the following cards
                <div className="wizardHand"> {wizard.hand.map((card) =>
                        <div className={this.cardClass(card.UID)}
                            onClick={() => this.props.playing(card.UID)} key={card.UID}> {card.name}
                        </div>)}
                    </div>
                </div>)}
                </div>
        );
    }
}

export default Hand;
