    constructor() {
        super();
        this.state = {
            currentSelection: 0
        };
    }
    grabdetails() {

    }
    componentDidMount() {
        this.grabdetails()
    }

    render() {
        return (
            <div className="Wizard">
                <div>
                    {this.props.wizards.map((wizard, i) => <div key={i}> {wizard.number}{wizard.character} has {wizard.health} health, {wizard.gold} gold, {wizard.lightning} lightning
                    <Hand playing={this.props.playing} hand={wizard.hand} />
                    </div> )}
                </div>
            </div>
        );
    }
}

export default Wizards;
