import React, { Component } from 'react';

class Board extends Component {
    constructor() {
        super();
        this.state = {
            board : {
                villians: [],
                eventDeck: [],
                location: {
                    name: "",
                    villianCapacity: 0,
                    villianCurrent: 0,
                    locationNumber: 0,
                    family : ""
                    },
                locationCount: 1,
                locationLimit: 3},
            render : true
        }
      }
    componentDidMount() {
        fetch('/board')
          .then(res => res.json())
          .then(board => this.setState({board}, () => console.log('Customers fetched...', board)));
      }
    render () {
        return (
            <div>
<h3>
    The Current Location is {this.state.board.location.name}
    </h3>
<div>
    state of villiany is 
{this.state.board.location.villianCurrent} out of {this.state.board.location.villianCapacity}
    </div>
                </div>
        );
    }
}

export default Board;
