class VillianBoard extends Component {
    constructor() {
        super();
        this.state = {}
    }
    render() {
        var villianCapacity = []
        for (let i = 0; i < this.props.villianCapacity; i++) {
            villianCapacity.push(<div> V </div>)
        }
        var villianCurrent = []
        for (let i = 0; i < this.props.villianCurrent; i++) {
            villianCurrent.push("V")
        }
        return (
            <div>
                {villianCapacity}
            </div>
        )
    };
}

export default VillianBoard;
